import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { packageGiftCard } from '../../utils/HomeData';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import { responsiveSlider } from '../../utils/responsiveSlider';


const PackageGiftCard = ({ card, btn }) => {
    return (
        <View style={styles.card_container}>
            <View style={styles.card_heading}>
                <Text style={styles.cardStyle}>{card}</Text>
                <Text style={styles.card_btnStyle}>{btn}</Text>
            </View>
            <Carousel
                swipeable={false}
                responsive={responsiveSlider}
                draggable={false}
                infinite={true}
                autoPlay={true}
                autoPlaySpeed={4000}
                slidesToSlide={1}
                keyBoardControl={true}
                containerClass="carousel-container"
                dotListClass="custom-dot-list-style"
                itemClass="carousel-item-padding-40-px"
            >
                <View style={styles.card_box}>
                    {packageGiftCard.map((data) => (
                        <View style={styles.card__box}>
                            <Image source={data.src} style={styles.card_img} />
                        </View>
                    ))}
                </View>
            </Carousel>
        </View>
    );
}

const styles = StyleSheet.create({
    card_container: {
        width: '100vw',
        backgroundColor: '#FBFCFE',
    },
    card_heading: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: "10px",
        margin: '20px',
    },
    cardStyle: {
        color: "#102C56",
        fontSize: '19px',
        lineHeight: "14px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    card_btnStyle: {
        color: "#F98700",
        fontSize: '17px',
        lineHeight: "18px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    card_box: {
        width: '90vw',
        flexDirection: 'row',
        justifyContent: "space-between"
    },
    card__box: {
        margin: '5px',
        padding: '5px',
        borderRadius: '10px',
    },
    card_img: {
        width: '110px',
        height: '70px',
        borderRadius: '10px',
    }
})

export default PackageGiftCard;
